function scrollNav(){document.querySelectorAll(".navegacion-principal a").forEach((e=>{e.addEventListener("click",(e=>{e.preventDefault();const t=e.target.getAttribute("href");document.querySelector(t).scrollIntoView({behavior:"smooth"})}))}))}function resaltarEnlaces(){document.addEventListener("scroll",(()=>{const e=document.querySelectorAll("section"),t=document.querySelectorAll(".navegacion-principal a");let a="";e.forEach((e=>{const t=e.offsetTop,n=e.clientHeight;window.scrollY>=t-n/3&&(a=e.id)})),t.forEach((e=>{e.classList.remove("active"),e.getAttribute("href")==="#"+a&&e.classList.add("active")}))}))}function navegacionFija(){const e=document.querySelector(".header"),t=document.querySelector(".sobre-festival");window.addEventListener("scroll",(()=>{t.getBoundingClientRect().bottom<1?e.classList.add("header-fixed"):e.classList.remove("header-fixed")}))}function crearGaleria(){const e=document.querySelector(".galeria-imagenes");for(let t=1;t<=16;t++){const a=document.createElement("PICTURE");a.innerHTML=`\n            <source srcset="dist/img/gallery/thumb/${t}.avif" type="image/avif">\n            <source srcset="dist/img/gallery/thumb/${t}.webp" type="image/webp">\n            <img loading="lazy" width="200" height="300" src="dist/img/gallery/thumb/${t}.jpg" alt="imagen galeria">\n        `,a.onclick=()=>{mostrarImagen(t)},e.appendChild(a)}}function mostrarImagen(e){const t=document.createElement("PICTURE");t.innerHTML=`\n            <source srcset="dist/img/gallery/full/${e}.avif" type="image/avif">\n            <source srcset="dist/img/gallery/full/${e}.webp" type="image/webp">\n            <img loading="lazy" width="200" height="300" src="dist/img/gallery/full/${e}.jpg" alt="imagen galeria">\n        `;const a=document.createElement("DIV");a.classList.add("modal"),a.onclick=cerrarModal;const n=document.createElement("BUTTON");n.classList.add("bt-cerrar"),n.textContent="X",n.onclick=cerrarModal,a.appendChild(t),a.appendChild(n);const r=document.querySelector("body");r.classList.add("overflow-hidden"),r.appendChild(a)}function cerrarModal(){const e=document.querySelector(".modal");e.classList.add("fade-out"),setTimeout((()=>{e?.remove();document.querySelector("body").classList.remove("overflow-hidden")}),500)}document.addEventListener("DOMContentLoaded",(function(){crearGaleria(),navegacionFija(),resaltarEnlaces(),scrollNav()}));